# -*- coding: utf-8 -*-
import os
files = os.listdir(os.path.dirname(__file__))
__all__ = [filename[:-3] for filename in files if not filename.startswith('__') and filename.endswith('.py')]

def scrapersList():
	return __all__

if __name__ == '__main__':
	print('Scrapers:')
	print(scrapersList())
