import os
import sys
import xbmcaddon

TORRSERVED_HOST = "http://127.0.0.1:8090"
ADDON = xbmcaddon.Addon()
ADDON_ID = ADDON.getAddonInfo("id")
ADDON_NAME = ADDON.getAddonInfo("name")
ADDON_ICON = ADDON.getAddonInfo("icon")
try:
    ADDON_PATH = os.path.abspath(os.path.join(os.path.dirname(os.path.realpath(__file__).decode(sys.getfilesystemencoding(), 'ignore')), "..", "..", ".."))
except:
    ADDON_PATH = ADDON.getAddonInfo("path")

def init():
    global TORRSERVED_HOST

    MODULE = xbmcaddon.Addon('script.module.torrserver')

    try:
        TORRSERVED_HOST = "http://"+MODULE.getSetting("host")+":"+MODULE.getSetting("port")
    except:
        pass


init()
